import React, { useState } from 'react'
import Paper from '@mui/material/Paper'
import Grid from '@mui/material/Grid'
import Typography from '@mui/material/Typography'
import ToggleButton from '@mui/material/ToggleButton'
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup'
import { makeStyles } from '@mui/styles'

const useStyles = makeStyles((theme) => ({
   // ToggleButton
   root: {
    background: "white",
    color: "darkblue",
    textTransform: "none",
    borderRight: "1pt solid lightgray"
  },
  selected: {
    background: "blue !important",
    color: "white !important"
  },
  // Paper
  paperRoot: {
    margin: "1em 0",
  }
}))

const ResultsButtons = () => {

  const [ selectedButton, setSelectedButton ] = useState("JSON")

  const classes = useStyles()

  const buttonLabels = ['JSON', 'Basic format', 'Full format']

  const handleChange = (event) => {
    const value = event.currentTarget.value
    setSelectedButton(value)
    props.handleFormat(value)
  }

  const componentDidMount= () => {
    setSelectedButton(props.selectedButton)
  }

  return (
    <Paper elevation={0} classes={{root:classes.paperRoot}}>
      <Grid container spacing={16}>
        <Grid item>
          <Typography variant="h6">
            Results:
          </Typography>
        </Grid>
        <Grid item>
          {props.showButtons &&
            <ToggleButtonGroup value={selectedButton} exclusive onChange={handleChange}>
                {buttonLabels.map((item) => (
                  <ToggleButton 
                    key={item} 
                    value={item} 
                    classes={{root:classes.root, selected:classes.selected}}
                  >
                    {item}
                  </ToggleButton>                  
                ))}
            </ToggleButtonGroup>
          }
        </Grid>
      </Grid>
    </Paper>
  )
}

export default ResultsButtons
