import React, { useEffect } from 'react'
import ReactGA from 'react-ga'
import QuerySelector from './QuerySelector.jsx'
import StnMetaInput from './StnMetaInput.jsx'
import StnDataInput from './StnDataInput.jsx'
import MultiStnDataInput from './MultiStnDataInput.jsx'
import StnHourlyInput from './StnHourlyInput.jsx'
import GridDataInput from './GridDataInput.jsx'
import GridData2Input from './GridData2Input.jsx'
import GeneralInput from './GeneralInput.jsx'
import Output from './Output'
import HelpTable from './HelpTable'
import Footer from './Footer.jsx'
//import QueryExplain from './QueryExplain'

const acis_servers = {
  StnMeta: "https://data.rcc-acis.org/StnMeta",
  StnData: "https://data.rcc-acis.org/StnData",
  MultiStnData: "https://data.rcc-acis.org/MultiStnData",
  GridData: "https://data.rcc-acis.org/GridData",
  GridData2: "https://grid2.rcc-acis.org/GridData",
  StnHourly: "https://data.nrcc.rcc-acis.org/StnData",
  General: "https://data.rcc-acis.org/General/",
}
const query_types = Object.keys(acis_servers)


const App = () => {

  state = {
    wstype: 'StnData',
    input_params: {},
    generalArea: '',
    helpFor: null,
    server: 'Any',
  }

  useEffect(() => {
    ReactGA.initialize('UA-47226172-1')
    ReactGA.pageview('Builder-pageview')    
  }, [])

  handleQueryChange = (value) => {
    setState({ 
      wstype: value, 
      input_params: {},
      generalArea: '',
      helpFor: null,
    })
  }

  handleInputStringChange = (inputString) => {
    if (inputString.length > 0) {
      try {
        setState({
          input_params: JSON.parse(inputString)
        })
      } catch {
        return
      }
    }
  }

  updateInputParams = (changes) => {
    let input_params = {...state.input_params}
    Object.keys(changes).forEach((key) => {
      const value = changes[key]
      if (value || (typeof value === 'object' && Object.keys(value).length > 0)) {
        input_params[key] = value
      } else {
        delete input_params[key]
      }
    })
    setState({ input_params })
  }

  updateAppState = (change) => {
    setState(change)
  }


  let acisServer = acis_servers[state.wstype]
  const datarccindex = acisServer.indexOf('data.rcc-acis')
  if (datarccindex !== -1 && state.server !== 'Any') {
    acisServer = acisServer.substring(0,datarccindex+4) + "." + state.server.toLowerCase() + acisServer.substring(datarccindex+4)
  }

  return (
    <div>
      <QuerySelector
        query_types={query_types}
        wstype={state.wstype}
        handleChange={handleQueryChange}
      />
      {state.wstype === "StnMeta" && 
        <StnMetaInput 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.wstype === "StnData" && 
        <StnDataInput 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.wstype === "MultiStnData" && 
        <MultiStnDataInput 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.wstype === "StnHourly" && 
        <StnHourlyInput 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.wstype === "GridData" && 
        <GridDataInput 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.wstype === "GridData2" && 
        <GridData2Input 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.wstype === "General" && 
        <GeneralInput 
          updateInputParams={updateInputParams}
          updateAppState={updateAppState}
          input_params={state.input_params}
        />
      }
      {state.helpFor && 
        <HelpTable 
          id={state.helpFor}
        /> 
      }
      <Output
        input_params={state.input_params} 
        changedInput={handleInputStringChange}
        generalArea={state.generalArea}
        acisServer={acisServer}
        server={state.server}
      />
      {state.wstype !== 'StnHourly' && state.wstype !== 'GridData2' &&
        <Footer 
          server={state.server}
          updateAppState={updateAppState}
        />
      }
    </div>
  )

}

export default App
