import { createTheme } from '@mui/material/styles';

const theme = createTheme(({
  components: {
    MuiToggleButton: {
      styleOverrides: {
        root: {
          background:"lightcyan",
          color:"darkblue",
          textTransform:"none",
          border:"1px solid lightgray !important",      
          "&.Mui-selected": {
            background: "blue !important",
            color: "white !important"
          },
          "&:hover": {
            backgroundColor: "lightblue",
          },
        },
      }
    },    
    MuiButton: {
      styleOverrides: {
        root: {
          background:"lightcyan",
          color:"darkblue",
          border:"1px solid lightgray",
          marginTop:"0.5em",
        },
      },
      variants: [
        {
          props: { variant: "verysmall" },
          style: {
            textTransform: 'none',
            lineHeight: "1em",
            fontSize: "75%",
            fontWeight: "normal",
          },
        },
      ],
    },
    MuiMenuItem: {
      variants: [
        {
          props: { variant: "verysmall" },
          style: {
            height: "2em",
            fontSize: "75%"
          },
        },
      ],
    }, 
    MuiPaper: {
      variants: [
        {
          props: { variant: "helpPaper" },
          style: {
            border:"1px solid gray", 
            margin:"1em 10%",
            padding:"3px",
            backgroundColor:"lightcyan",
          },
        },
        {
          props: { variant: "explainPaper" },
          style: {
            border:"1px solid lightgray", 
            margin:"0", 
            padding:"10px", 
            backgroundColor:"#f4ffff",
            width:"85%",
          },
        },
        {
          props: { variant: "resultsPaper" },
          style: {
            border:"1px solid lightgray", 
            padding:"0 5px", 
            fontSize:"95%",
          },
        },
      ],
    },   
    MuiOutlinedInput: {
      styleOverrides: {
        input: {
          paddingTop:"7px", 
          paddingBottom:"7px", 
          fontSize:"80%", 
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          "&.Mui-required": {
            color: "limegreen"
          },
        },
      },
    },
    MuiSwitch: {
      styleOverrides: {
        switchBase: {
          color: "lightcyan"
        }
      }
    },
    MuiChip: {
      styleOverrides: {
        root: {
          background: "lightcyan",
          marginBottom:"0.5em", 
          marginTop:"0.5em",
        }
      }
    },
    MuiTableCell: {
      styleOverrides: {
        root: {
          fontSize:"80%",
          padding:"2px 1em",
        },
      },
    },
    MuiTypography: {
      styleOverrides: {
        h5: {
          color: "darkblue",
        },
      },
    },
  },
}))

export default theme
