import React from 'react'
import Paper from '@mui/material/Paper'
import Grid from '@mui/material/Grid'
import Typography from '@mui/material/Typography'
import ToggleButton from '@mui/material/ToggleButton'
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup'
import { makeStyles } from '@mui/styles'

const useStyles = makeStyles((theme) => ({
   // ToggleButton selected
  selected: {
    background: "blue !important",
    color: "white !important"
  },
  // Paper
  paper: {
    margin: "1em 0",
  }
}))

const ResultsButtons = (props) => {
  const classes = useStyles()
  const buttonLabels = ['JSON', 'Basic format', 'Full format']

  return (
    <Paper elevation={0} classes={{root:classes.paper}}>
      <Grid container spacing={8}>
        <Grid item>
          <Typography variant="h6">
            Results:
          </Typography>
        </Grid>
        <Grid item>
          {props.showButtons &&
            <ToggleButtonGroup 
              value={props.selectedButton} 
              exclusive 
              onChange={e => props.handleFormat(e.target.value)}
            >
              {buttonLabels.map((item) => (
                <ToggleButton 
                  key={item} 
                  value={item} 
                  classes={{selected:classes.selected}}
                >
                  {item}
                </ToggleButton>                  
              ))}
            </ToggleButtonGroup>
          }
        </Grid>
      </Grid>
    </Paper>
  )
}

export default ResultsButtons
