import React, { useState, useEffect } from 'react'
import Paper from '@mui/material/Paper'
import Grid from '@mui/material/Grid'
import Typography from '@mui/material/Typography'
import ToggleButton from '@mui/material/ToggleButton'
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup'
import { withStyles } from '@mui/styles'

const styles = {
  // classes for ToggleButton
  root: {
    background:"white",
    color:"darkblue",
    textTransform:"none",
    borderRight:"1pt solid lightgray"
  },
  selected: {
    background: "blue !important",
    color:"white !important"
  },
  // class for Paper
  paperRoot: {
    margin: "0 0 2em"
  },
  // class for page title typography
  titleRoot: {
    color: "darkblue"
  },
}

const QuerySelector = (props) => {
  const [wstype, setWstype] = useState('')

  useEffect(() => {
    setWstype(props.wstype)
  }, [])

  const handleChange = (event, value) => {
    if (value) {
      setWstype(value)
      props.handleChange(value)
    }
  }

  const {classes} = props

  return (
    <Paper elevation={0} className={classes.paperRoot}>
      <Grid container spacing={0}>
        <Grid item xs={12} sm={4}>
          <Typography variant="h5" className={classes.titleRoot}>
            ACIS Query Builder
          </Typography>
        </Grid>
        <Grid item>
          <ToggleButtonGroup value={wstype} exclusive onChange={handleChange}>
            {props.query_types.map((item) => (
              <ToggleButton 
                key={item} 
                value={item}
                className={`classes.root classes.selected`}
              >
                {item}
              </ToggleButton>                  
            ))}
          </ToggleButtonGroup>
        </Grid>
      </Grid>
    </Paper>
  )
}

export default withStyles(styles)(QuerySelector)
