import React, { useState, useEffect } from 'react'
import Typography from '@mui/material/Typography'
import Paper from '@mui/material/Paper'
import { buildExplanation } from './builders.js'

const QueryExplain = (props) => {
  const  [ exptext, setExptext ] = useState("")

  useEffect(() => {
    const exptext = buildExplanation(props.input_params)
    setExptext(exptext)
  }, [props.input_params])

  return (
    <Paper elevation={0} style={{marginTop:"1em"}}>
      {exptext &&
        <div>
          <Paper 
            elevation={3} 
            style={{
              border:"1px solid lightgray", 
              margin:"0", 
              padding:"10px", 
              width:"85%",
              backgroundColor:"#f4ffff"
            }}
          >
          <Typography variant="body2" style={{fontWeight:"bold"}}>
            Expected return:
          </Typography>
          <Typography style={{fontSize:"80%"}}>
            {exptext}
          </Typography>
          </Paper>
        </div>
      }
    </Paper>
  )
}

export default QueryExplain
