  // preocess duration/reduce portion
  let duration = elems.hasOwnProperty('duration') ? elems.duration : 1
  const reduce = elems.hasOwnProperty('reduce') ? (typeof elems.reduce === 'object' ? elems.reduce.reduce : elems.reduce) : null
  if (duration === 'dly' || duration === 'mly' || duration === 'yly') {
    duration = 1
  } else if (!isNaN(Number(duration))) {
    duration = Number(duration)
  }
  if ((duration !== 1 || intfreq !== 'day') && !reduce) {
    return noreduceMessage
  } else if (typeof duration === 'string' && !durationText.hasOwnProperty(duration)) {
    return baddurationlMessage
  } else if (duration === 'std' && !elems.hasOwnProperty("season_start")) {
    return noseasonstartMessage
  } else if (duration !== 1 || intfreq !== 'day') {
    const durphrase = typeof duration === 'string' ? durationText[duration] : (duration + "-" + intfreq)
    explanation += "Each data value returned for this query element will be a " + durphrase + " " + reduce
    // include season_start when appropriate
    if (duration === 'std') {
      if (typeof elems.season_start === "string") {
        elems.season_start = elems.season_start.split("-").map(item => { return Number(item) })
      }
      if (elems.season_start.length === 1) {
        elems.season_start.push(1)
      }
      const season_start = monthName[elems.season_start[0]-1] + " " + elems.season_start[1]
      explanation += " since " + season_start
    }
    explanation += ". "
  }
  const intphrase = intcnt === 1 ? inttype : (intcnt + " " + inttype + "s")
  explanation += "Results will consist of one value every " + intphrase
  // date portion
  if ((params.hasOwnProperty('sdate') && params.hasOwnProperty('edate')) || 
      params.hasOwnProperty('date') ||
      (elems.hasOwnProperty('sdate') && elems.hasOwnProperty('edate'))) {
    const sdate = elems.sdate || params.sdate || params.date
    const edate = elems.edate || params.edate || null
    let sdatestr = formatDate(sdate, intfreq)
    let edatestr = edate ? formatDate(edate, intfreq) : 'na'
    if (!sdatestr || !edatestr) {
      return baddateMessage
    }
    if (intfreq === 'day' && intpos < 2) {
      const sdparts = sdatestr.split(", ")
      const edparts = edatestr.split(", ")
      edatestr = sdparts[0] + ", " + edparts[1]
    } else if (intfreq === 'month' && intpos < 1) {
      const sdparts = sdatestr.split(" ")
      const edparts = edatestr.split(" ")
      edatestr = sdparts[0] + " " + edparts[1]
    }
    const datephrase = elems.hasOwnProperty('reduce') ? "the periods ending " : ""
    explanation += " for " + datephrase + sdatestr + (edatestr !== 'na' ? " through " + edatestr : "")
  }
  explanation += ". " 

  return explanation
}

export function clearElementKeys(elementKeys) {
  let clearedKeys = {}
  elementKeys.forEach((e) => {
    clearedKeys = {...clearedKeys, ...{[e]:""}}
  })
  return clearedKeys
}

export function unbuildElements(dselems, elementKeys) {
  var lastParsedElems
  try {
    const parsedElems = JSON.parse(dselems)
    lastParsedElems = parsedElems.at(-1)
    if (lastParsedElems.hasOwnProperty('reduce') && typeof lastParsedElems.reduce === 'object') {
      if (lastParsedElems.reduce.hasOwnProperty('add')) {
        lastParsedElems = {...lastParsedElems, ...{reduce_add: lastParsedElems.reduce.add}}
      }
      if (lastParsedElems.reduce.hasOwnProperty('n')) {
        lastParsedElems = {...lastParsedElems, ...{reduce_n: lastParsedElems.reduce.n}}
      }
      if (lastParsedElems.reduce.hasOwnProperty('run_maxmissing')) {
        lastParsedElems = {...lastParsedElems, ...{reduce_run_maxmissing: lastParsedElems.reduce.run_maxmissing}}
      }
      lastParsedElems = {...lastParsedElems, ...{reduce: lastParsedElems.reduce.reduce}}
    }
    if (lastParsedElems.hasOwnProperty('smry') && typeof lastParsedElems.smry === 'object') {
      if (lastParsedElems.smry.hasOwnProperty('add')) {
        lastParsedElems = {...lastParsedElems, ...{smry_add: lastParsedElems.smry.add}}
      }
      if (lastParsedElems.smry.hasOwnProperty('n')) {
        lastParsedElems = {...lastParsedElems, ...{smry_n: lastParsedElems.smry.n}}
      }
      if (lastParsedElems.smry.hasOwnProperty('run_maxmissing')) {
        lastParsedElems = {...lastParsedElems, ...{smry_run_maxmissing: lastParsedElems.smry.run_maxmissing}}
      }
      lastParsedElems = {...lastParsedElems, ...{smry: lastParsedElems.smry.reduce}}
    }
  } catch {
    lastParsedElems = {}
  }
  const clearedKeys = clearElementKeys(elementKeys)
  return {...clearedKeys, ...lastParsedElems}
}

export function basicFormat(results_json) {
  let results_string = ""
  let dataimage = ""
  Object.keys(results_json).forEach(key => {
    if (key === 'data' && results_json[key].includes("image/png;base64")) {
      dataimage = results_json[key]
    } else if (Array.isArray(results_json[key])) {
      results_string += key + ":\n"
      results_json[key].forEach(item => {
        results_string += " " + JSON.stringify(item) + "\n"
      }) 
    } else {
      results_string += key + ":\n"
      results_string += " " + JSON.stringify(results_json[key]) + "\n"
    }
  })
  return {results_string: results_string, dataimage: dataimage}
}

export function checkHasInterval(update) {
  if (update.length > 0 && update.includes("{")) {
    try {
      let updateElems = JSON.parse(update)
      let cond = false
      updateElems.forEach((ue) => {
        if (ue.hasOwnProperty('interval') && ue.interval.length) {
          cond = true
        }
      })
      return cond
    } catch {
      console.log('Error parsing elems update: ' + update)
      return false
    }
  } else {
    return false
  }
}

export function checkElemsError(strelems) {
  try {
    if (strelems.includes("{")) {
      // check for valid json
      JSON.parse(strelems)
    }
    return false
  } catch {
    return true
  }    
}

export function updateState(datafields, elementKeys, input_params, resetElemsBuilder) {
  let newstate= {}
  let checkHasIntervalStatus = false
  let checkElemsErrorStatus = false
  datafields.forEach((key) => {
    if (input_params.hasOwnProperty(key)) {
      if (key === 'elems') {
        const strelems = typeof input_params.elems === 'object' ? JSON.stringify(input_params.elems) : input_params.elems
        newstate = ({...newstate, ...{elems: strelems}})
        checkHasIntervalStatus = checkHasInterval(strelems)
        checkElemsErrorStatus = checkElemsError(strelems)
        if (resetElemsBuilder && strelems.length > 0) {
          const lastParsedElems = unbuildElements(strelems, elementKeys)
          newstate = {...newstate, ...lastParsedElems}
        }
      } else {
        newstate= ({...newstate, ...{[key]: input_params[key]}})
      }
    } else {
      newstate = ({...newstate, ...{[key]: ''}})
    }
  })
  return {newstate, checkHasIntervalStatus, checkElemsErrorStatus}
}
