import { createTheme } from '@mui/material/styles';

const theme = createTheme(({
  components: {
    MuiToggleButton: {
      styleOverrides: {
        root: {
          background:"lightcyan",
          color:"darkblue",
          textTransform:"none",
          border:"1px solid lightgray !important",      
        },
      }
    },    
    MuiButton: {
      styleOverrides: {
        root: {
          background:"lightcyan",
          color:"darkblue",
          textTransform:"none",
          border:"1px solid lightgray",
        },
      }
    },
    MuiSwitch: {
      styleOverrides: {
        switchBase: {
          color: "lightcyan"
        }
      }
    },
    MuiChip: {
      styleOverrides: {
        root: {
          background: "lightcyan",
          marginBottom:"0.5em", 
          marginTop:"0.5em",
        }
      }
    },
  },
}))

export default theme
