import { createTheme } from '@mui/material/styles';

const theme = createTheme(({
  components: {
    MuiToggleButton: {
      styleOverrides: {
        root: {
          background:"lightcyan",
          color:"darkblue",
          textTransform:"none",
          border:"1px solid lightgray !important",      
        },
      }
    },    
    MuiButton: {
      styleOverrides: {
        root: {
          background:"lightcyan",
          color:"darkblue",
          border:"1px solid lightgray",
        },
      },
      variants: [
        {
          props: { variant: "verysmall" },
          style: {
            textTransform: 'none',
            lineHeight: "1",
            fontSize: "75%",
            fontWeight: "normal",
          },
        },
      ],
    },
    MuiMenuItem: {
      variants: [
        {
          props: { variant: "verysmall" },
          style: {
            height: "18px",
            fontSize: "75%"
          },
        },
      ],
    },    
    MuiSwitch: {
      styleOverrides: {
        switchBase: {
          color: "lightcyan"
        }
      }
    },
    MuiChip: {
      styleOverrides: {
        root: {
          background: "lightcyan",
          marginBottom:"0.5em", 
          marginTop:"0.5em",
        }
      }
    },
  },
}))

export default theme
